mapper.select:
      select rnum, hname, coun_sche_address, coun_sche_content, coun_sche_startdate 
          from (
              select ROWNUM as rnum, hname, coun_sche_address, coun_sche_content, coun_sche_startdate 
              from (
			      select hname, coun_sche_address, coun_sche_content, coun_sche_startdate 
				  from counselingschedule INNER JOIN hospital on coun_sche_dln = hdln and coun_sche_address = haddress
				  where hname like '%'|| #{searchBar} ||'%'
				  and TO_CHAR(coun_sche_startdate,'yyyy-mm-dd') between #{sdate} and #{edate}
				  and TO_CHAR(coun_sche_startdate,'yyyy-mm-dd HH24:MI:SS') > TO_CHAR(SYSDATE,'yyyy-mm-dd HH24:MI:SS')
				  order by to_char(coun_sche_startdate,'yyyymmdd HH24:MI:SS')
          		)
          where rownum <= #{endRowNo}
          )
      where rnum >= #{startRowNo}

mapper.update:
update counselingschedule
set coun_sche_startdate=#{counScheStartdate}
where coun_sche_id=#{counScheId}

update progress
set pdate=#{counScheStartdate}, penddate=#{counScheStartdate}
where paddress=#{counScheAddress} and pdate=#{counScheStartdate}
