mapper.select:

select hdln, userid, uaddress, hname, uphonenumber, umembershipdate, uname
         from (
             select hdln, userid, uaddress, hname, uphonenumber, umembershipdate, uname
             from (
                 SELECT hdln, userid, uaddress, hname, uphonenumber, umembershipdate, uname
                 FROM users, hospital 
                 WHERE uaddress = haddress AND udln = hdln
                  )
         where rownum <= 5
         )
     where rownum >= 1
